//fade in on load
$(document).ready(function(){
	$('body').css('display', 'none');
	$('.main').css('display', 'none');
	$('.main-photo').css('display', 'none');
	$('.rest-content').css('display', 'none');
	$('body').fadeIn(400);
	$('.main-photo').delay(500).fadeIn(600);
	$('.main').delay(1000).fadeIn(500);
	$('.rest-content').delay(1500).fadeIn(400);
	ustawslajd(1);
	ustawslajd2(6);
	ustawslajd3(11);
}); 

// Sticky Navbar
var navbar = document.getElementById("navbar");
var sticky = navbar.offsetTop;
window.onscroll = function() {myFunction()};
function myFunction() {
  if (window.pageYOffset > (sticky)) {
    navbar.classList.add("sticky");
  } else {
    navbar.classList.remove("sticky");
  }
} 
// Making navbar responsive
function showNavBar() {
  var x = document.getElementById("navbar");
  if (x.className === "topnav" || x.className === "topnav sticky" ) {
    x.className += " responsive";
  } else {
    x.className = "topnav";
  }
} 
//slider1
var timer1 =0;
var timer2= 0;
function ustawslajd(nrslajdu) {
		clearTimeout(timer1);
		clearTimeout(timer2); 
		numer = nrslajdu-1;
		schowaj();
		setTimeout("zmienslajd()", 500);
}
function schowaj() {
	$("#slider").fadeOut(500);
}
function zmienslajd() {
	numer++;
	if(numer>5) numer=1;
	var plik = "<img src=\"img/"+numer+".jpg\"  />";
	document.getElementById("slider").innerHTML = plik;
	$("#slider").fadeIn(500);
	timer1 = setTimeout("zmienslajd()", 5000);
	timer2 = setTimeout("schowaj();", 4500);
}
